contracts/Project.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title Project
 * @dev Manages fund release milestones with decentralized verification
 * @notice This contract handles proof submission, voting, and milestone approval
 */
contract Project {
    // ==================== STRUCTS ====================

    struct Milestone {
        string title;
        string description;
        uint256 requiredApprovals; // N-of-M threshold
        bool isCompleted;
        bool isApproved;
        uint256 approvalCount;
        uint256 rejectionCount;
        uint256 createdAt;
    }

    struct Proof {
        bytes32 beforeImageHash;    // SHA-256 hash of before image
        bytes32 afterImageHash;     // SHA-256 hash of after image
        string gpsCoordinates;      // "latitude,longitude"
        uint256 timestamp;
        address submitter;
        ProofStatus status;
    }

    enum ProofStatus {
        PENDING,
        UNDER_REVIEW,
        VERIFIED,
        REJECTED
    }

    // ==================== STATE VARIABLES ====================

    address public organizer;
    address public admin; // Backend wallet for system operations
    string public title;
    string public description;
    uint256 public fundingGoal; // In INR (stored as wei equivalent for calculation)
    uint256 public createdAt;

    Milestone[] public milestones;
    mapping(uint256 => Proof) public milestoneProofs; // milestoneId => Proof
    mapping(uint256 => mapping(address => bool)) public hasVoted; // milestoneId => verifier => voted
    mapping(address => bool) public isVerifier;
    mapping(address => bool) public isDonor;

    uint256 public verifierCount;
    uint256 public donorCount;

    // ==================== EVENTS ====================

    event MilestoneCreated(
        uint256 indexed milestoneId,
        string title,
        string description,
        uint256 requiredApprovals
    );

    event ProofSubmitted(
        uint256 indexed milestoneId,
        bytes32 beforeImageHash,
        bytes32 afterImageHash,
        string gpsCoordinates,
        address submitter
    );

    event VoteCast(
        uint256 indexed milestoneId,
        address indexed voter,
        bool approve,
        string comment
    );

    event MilestoneApproved(
        uint256 indexed milestoneId,
        uint256 approvalCount,
        uint256 timestamp
    );

    event MilestoneRejected(
        uint256 indexed milestoneId,
        uint256 rejectionCount,
        uint256 timestamp
    );

    event VerifierAdded(address indexed verifier);
    event VerifierRemoved(address indexed verifier);
    event DonorAdded(address indexed donor, uint256 amount);

    // ==================== MODIFIERS ====================

    modifier onlyOrganizer() {
        require(msg.sender == organizer, "Only organizer can perform this action");
        _;
    }

    modifier onlyVerifier() {
        require(isVerifier[msg.sender], "Only verifiers can perform this action");
        _;
    }

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }

    modifier validMilestone(uint256 _milestoneId) {
        require(_milestoneId < milestones.length, "Invalid milestone ID");
        _;
    }

    // ==================== CONSTRUCTOR ====================

    constructor(
        address _organizer,
        string memory _title,
        string memory _description,
        uint256 _fundingGoal
    ) {
        organizer = _organizer;
        admin = msg.sender;
        title = _title;
        description = _description;
        fundingGoal = _fundingGoal;
        createdAt = block.timestamp;
    }

    // ==================== VERIFIER MANAGEMENT ====================

    function addVerifier(address _verifier) external onlyAdmin {
        require(!isVerifier[_verifier], "Already a verifier");
        isVerifier[_verifier] = true;
        verifierCount++;
        emit VerifierAdded(_verifier);
    }

    function removeVerifier(address _verifier) external onlyAdmin {
        require(isVerifier[_verifier], "Not a verifier");
        isVerifier[_verifier] = false;
        verifierCount--;
        emit VerifierRemoved(_verifier);
    }

    // ==================== DONOR MANAGEMENT ====================

    function addDonor(address _donor, uint256 _amount) external onlyAdmin {
        if (!isDonor[_donor]) {
            isDonor[_donor] = true;
            donorCount++;
        }
        emit DonorAdded(_donor, _amount);
    }

    // ==================== MILESTONE MANAGEMENT ====================

    function createMilestone(
        string memory _title,
        string memory _description,
        uint256 _requiredApprovals
    ) external onlyOrganizer {
        require(_requiredApprovals > 0, "Required approvals must be > 0");

        milestones.push(Milestone({
            title: _title,
            description: _description,
            requiredApprovals: _requiredApprovals,
            isCompleted: false,
            isApproved: false,
            approvalCount: 0,
            rejectionCount: 0,
            createdAt: block.timestamp
        }));

        emit MilestoneCreated(
            milestones.length - 1,
            _title,
            _description,
            _requiredApprovals
        );
    }

    // ==================== PROOF SUBMISSION ====================

    function submitProof(
        uint256 _milestoneId,
        bytes32 _beforeImageHash,
        bytes32 _afterImageHash,
        string memory _gpsCoordinates
    ) external onlyOrganizer validMilestone(_milestoneId) {
        Milestone storage milestone = milestones[_milestoneId];
        require(!milestone.isCompleted, "Milestone already completed");
        require(milestoneProofs[_milestoneId].timestamp == 0, "Proof already submitted");

        milestoneProofs[_milestoneId] = Proof({
            beforeImageHash: _beforeImageHash,
            afterImageHash: _afterImageHash,
            gpsCoordinates: _gpsCoordinates,
            timestamp: block.timestamp,
            submitter: msg.sender,
            status: ProofStatus.PENDING
        });

        emit ProofSubmitted(
            _milestoneId,
            _beforeImageHash,
            _afterImageHash,
            _gpsCoordinates,
            msg.sender
        );
    }

    // ==================== VERIFICATION & VOTING ====================

    function voteOnProof(
        uint256 _milestoneId,
        bool _approve
    ) external onlyVerifier validMilestone(_milestoneId) {
        require(!hasVoted[_milestoneId][msg.sender], "You have already voted");
        require(milestoneProofs[_milestoneId].timestamp > 0, "No proof submitted");

        Milestone storage milestone = milestones[_milestoneId];
        require(!milestone.isCompleted, "Milestone already completed");

        hasVoted[_milestoneId][msg.sender] = true;

        if (_approve) {
            milestone.approvalCount++;
        } else {
            milestone.rejectionCount++;
        }

        emit VoteCast(_milestoneId, msg.sender, _approve, "");

        // Check if threshold is met
        _checkApprovalThreshold(_milestoneId);
    }

    function _checkApprovalThreshold(uint256 _milestoneId) internal {
        Milestone storage milestone = milestones[_milestoneId];

        // Check if required approvals threshold is met
        if (milestone.approvalCount >= milestone.requiredApprovals) {
            milestone.isApproved = true;
            milestone.isCompleted = true;
            milestoneProofs[_milestoneId].status = ProofStatus.VERIFIED;

            emit MilestoneApproved(
                _milestoneId,
                milestone.approvalCount,
                block.timestamp
            );
        }
        // Optional: Auto-reject if rejections exceed a threshold
        else if (milestone.rejectionCount >= milestone.requiredApprovals) {
            milestone.isCompleted = true;
            milestoneProofs[_milestoneId].status = ProofStatus.REJECTED;

            emit MilestoneRejected(
                _milestoneId,
                milestone.rejectionCount,
                block.timestamp
            );
        }
    }

    // ==================== VIEW FUNCTIONS ====================

    function getMilestone(uint256 _milestoneId) external view validMilestone(_milestoneId) 
        returns (
            string memory _title,
            string memory _description,
            uint256 _requiredApprovals,
            bool _isCompleted,
            bool _isApproved,
            uint256 _approvalCount,
            uint256 _rejectionCount
        ) 
    {
        Milestone memory m = milestones[_milestoneId];
        return (
            m.title,
            m.description,
            m.requiredApprovals,
            m.isCompleted,
            m.isApproved,
            m.approvalCount,
            m.rejectionCount
        );
    }

    function getProof(uint256 _milestoneId) external view validMilestone(_milestoneId)
        returns (
            bytes32 _beforeImageHash,
            bytes32 _afterImageHash,
            string memory _gpsCoordinates,
            uint256 _timestamp,
            address _submitter,
            ProofStatus _status
        )
    {
        Proof memory p = milestoneProofs[_milestoneId];
        return (
            p.beforeImageHash,
            p.afterImageHash,
            p.gpsCoordinates,
            p.timestamp,
            p.submitter,
            p.status
        );
    }

    function getMilestoneCount() external view returns (uint256) {
        return milestones.length;
    }

    function hasUserVoted(uint256 _milestoneId, address _user) external view returns (bool) {
        return hasVoted[_milestoneId][_user];
    }
}

contracts/ProjectFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./Project.sol";

/**
 * @title ProjectFactory
 * @dev Factory contract to deploy and manage Project contracts
 * @notice Creates new Project instances and maintains a registry
 */
contract ProjectFactory {
    // ==================== STATE VARIABLES ====================

    Project[] public projects;
    mapping(address => address[]) public organizerProjects; // organizer => project addresses
    mapping(address => bool) public isProject; // Check if address is a valid project

    address public admin;

    // ==================== EVENTS ====================

    event ProjectCreated(
        address indexed projectAddress,
        address indexed organizer,
        string title,
        uint256 fundingGoal,
        uint256 timestamp
    );

    // ==================== MODIFIERS ====================

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }

    // ==================== CONSTRUCTOR ====================

    constructor() {
        admin = msg.sender;
    }

    // ==================== PROJECT CREATION ====================

    function createProject(
        address _organizer,
        string memory _title,
        string memory _description,
        uint256 _fundingGoal
    ) external returns (address) {
        // Create new Project contract
        Project newProject = new Project(
            _organizer,
            _title,
            _description,
            _fundingGoal
        );

        address projectAddress = address(newProject);

        // Store in registry
        projects.push(newProject);
        organizerProjects[_organizer].push(projectAddress);
        isProject[projectAddress] = true;

        emit ProjectCreated(
            projectAddress,
            _organizer,
            _title,
            _fundingGoal,
            block.timestamp
        );

        return projectAddress;
    }

    // ==================== VIEW FUNCTIONS ====================

    function getProjects() external view returns (Project[] memory) {
        return projects;
    }

    function getProjectCount() external view returns (uint256) {
        return projects.length;
    }

    function getProjectsByOrganizer(address _organizer) external view returns (address[] memory) {
        return organizerProjects[_organizer];
    }

    function getProjectDetails(uint256 _index) external view returns (
        address projectAddress,
        address organizer,
        string memory title,
        string memory description,
        uint256 fundingGoal,
        uint256 createdAt
    ) {
        require(_index < projects.length, "Invalid project index");

        Project project = projects[_index];

        return (
            address(project),
            project.organizer(),
            project.title(),
            project.description(),
            project.fundingGoal(),
            project.createdAt()
        );
    }

    function isValidProject(address _projectAddress) external view returns (bool) {
        return isProject[_projectAddress];
    }
}

contracts/TaskEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * TaskEscrow - AUDIT TRAIL ONLY (No Crypto Payments)
 * 
 * This contract provides decentralized, immutable record-keeping for tasks.
 * Actual payments are handled OFF-CHAIN via Stripe in INR.
 * 
 * Purpose: Transparency & Trust without cryptocurrency complexity
 */
contract TaskEscrow {
    address public backend;  // Trusted backend that records events

    struct Task {
        address client;          // Client's wallet address (for identity)
        address worker;          // Worker's wallet address (for identity)
        uint256 amountINR;       // Amount in INR (reference only, not actual payment)
        TaskStatus status;
        uint256 createdAt;
        uint256 completedAt;
        string beforeImageHash;  // IPFS/SHA256 hash of before image
        string afterImageHash;   // IPFS/SHA256 hash of after image
        uint8 aiConfidence;      // AI confidence score (0-100)
        string stripePaymentIntentId;  // Link to actual Stripe payment
        string stripeTransferId;       // Link to Stripe transfer to worker
    }

    enum TaskStatus { 
        CREATED,          // Task posted
        ACCEPTED,         // Worker accepted
        WORK_SUBMITTED,   // Before/after images uploaded
        AI_VERIFIED,      // AI approved the work
        PAYMENT_RELEASED, // Stripe payment completed
        DISPUTED,         // Client disputed
        CANCELLED         // Task cancelled
    }

    // taskId (bytes32 hash) => Task data
    mapping(bytes32 => Task) public tasks;

    // Events for transparency
    event TaskCreated(
        bytes32 indexed taskId,
        address indexed client,
        uint256 amountINR,
        string stripePaymentIntentId,
        uint256 timestamp
    );

    event TaskAccepted(
        bytes32 indexed taskId,
        address indexed worker,
        uint256 timestamp
    );

    event WorkSubmitted(
        bytes32 indexed taskId,
        string beforeImageHash,
        string afterImageHash,
        uint256 timestamp
    );

    event AIVerified(
        bytes32 indexed taskId,
        uint8 confidence,
        bool approved,
        uint256 timestamp
    );

    event PaymentReleased(
        bytes32 indexed taskId,
        address indexed worker,
        uint256 amountINR,
        string stripeTransferId,
        uint256 timestamp
    );

    event TaskDisputed(
        bytes32 indexed taskId,
        string reason,
        uint256 timestamp
    );

    modifier onlyBackend() {
        require(msg.sender == backend, "Only backend can record");
        _;
    }

    constructor(address _backend) {
        backend = _backend;
    }

    // Record task creation (called by backend after Stripe escrow created)
    function recordTaskCreation(
        bytes32 taskId,
        address client,
        uint256 amountINR,
        string memory stripePaymentIntentId
    ) external onlyBackend {
        require(tasks[taskId].client == address(0), "Task already exists");

        tasks[taskId] = Task({
            client: client,
            worker: address(0),
            amountINR: amountINR,
            status: TaskStatus.CREATED,
            createdAt: block.timestamp,
            completedAt: 0,
            beforeImageHash: "",
            afterImageHash: "",
            aiConfidence: 0,
            stripePaymentIntentId: stripePaymentIntentId,
            stripeTransferId: ""
        });

        emit TaskCreated(taskId, client, amountINR, stripePaymentIntentId, block.timestamp);
    }

    // Record worker acceptance
    function recordTaskAcceptance(
        bytes32 taskId,
        address worker
    ) external onlyBackend {
        Task storage task = tasks[taskId];
        require(task.status == TaskStatus.CREATED, "Invalid status");

        task.worker = worker;
        task.status = TaskStatus.ACCEPTED;

        emit TaskAccepted(taskId, worker, block.timestamp);
    }

    // Record work submission with image hashes
    function recordWorkSubmission(
        bytes32 taskId,
        string memory beforeHash,
        string memory afterHash
    ) external onlyBackend {
        Task storage task = tasks[taskId];
        require(task.status == TaskStatus.ACCEPTED, "Invalid status");

        task.beforeImageHash = beforeHash;
        task.afterImageHash = afterHash;
        task.status = TaskStatus.WORK_SUBMITTED;
        task.completedAt = block.timestamp;

        emit WorkSubmitted(taskId, beforeHash, afterHash, block.timestamp);
    }

    // Record AI verification result
    function recordAIVerification(
        bytes32 taskId,
        uint8 confidence,
        bool approved
    ) external onlyBackend {
        Task storage task = tasks[taskId];
        require(task.status == TaskStatus.WORK_SUBMITTED, "Invalid status");

        task.aiConfidence = confidence;
        task.status = TaskStatus.AI_VERIFIED;

        emit AIVerified(taskId, confidence, approved, block.timestamp);
    }

    // Record payment release (called AFTER Stripe completes transfer)
    function recordPaymentRelease(
        bytes32 taskId,
        string memory stripeTransferId
    ) external onlyBackend {
        Task storage task = tasks[taskId];
        require(task.status == TaskStatus.AI_VERIFIED, "Invalid status");

        task.stripeTransferId = stripeTransferId;
        task.status = TaskStatus.PAYMENT_RELEASED;

        emit PaymentReleased(
            taskId,
            task.worker,
            task.amountINR,
            stripeTransferId,
            block.timestamp
        );
    }

    // Record dispute
    function recordDispute(
        bytes32 taskId,
        string memory reason
    ) external onlyBackend {
        Task storage task = tasks[taskId];
        require(
            task.status == TaskStatus.AI_VERIFIED || 
            task.status == TaskStatus.PAYMENT_RELEASED,
            "Invalid status"
        );

        task.status = TaskStatus.DISPUTED;

        emit TaskDisputed(taskId, reason, block.timestamp);
    }

    // Public audit function - ANYONE can verify task history
    function getTaskAudit(bytes32 taskId) external view returns (
        address client,
        address worker,
        uint256 amountINR,
        TaskStatus status,
        uint256 createdAt,
        uint256 completedAt,
        string memory beforeImageHash,
        string memory afterImageHash,
        uint8 aiConfidence,
        string memory stripePaymentIntentId,
        string memory stripeTransferId
    ) {
        Task memory task = tasks[taskId];
        return (
            task.client,
            task.worker,
            task.amountINR,
            task.status,
            task.createdAt,
            task.completedAt,
            task.beforeImageHash,
            task.afterImageHash,
            task.aiConfidence,
            task.stripePaymentIntentId,
            task.stripeTransferId
        );
    }

    // Verify image hash (anyone can check if images were tampered)
    function verifyImageHash(
        bytes32 taskId,
        string memory imageHash,
        bool isBefore
    ) external view returns (bool) {
        Task memory task = tasks[taskId];
        if (isBefore) {
            return keccak256(bytes(task.beforeImageHash)) == keccak256(bytes(imageHash));
        } else {
            return keccak256(bytes(task.afterImageHash)) == keccak256(bytes(imageHash));
        }
    }
}

